mapper.select:
SELECT faqno, adminno, faqtitle, faqcontent
FROM faq
WHERE faqno = #{faqno}

mapper.update:
UPDATE faq 
SET faqtitle=#{faqtitle}, faqcontent=#{faqcontent}
WHERE faqno=#{faqno}

mapper.select:
SELECT COUNT(*) as passwd_cnt
FROM faq
WHERE faqno=#{faqno} AND adminpass=#{adminpass}

SELECT COUNT(*) as cnt FROM faq
WHERE faqno=#{faqno}
WHERE (faqtitle LIKE '%' || #{faqword} || '%' OR faqcontent LIKE '%' || #{faqword} || '%' OR faqword LIKE '%' || #{faqword} || '%')
