mapper.select:
SELECT musicno, title, singer, album, genre, likesu, playsu, youtube, albumno, singerno, genreno, r FROM ( SELECT musicno, title, singer, album, genre, likesu, playsu, youtube, albumno, singerno, genreno, rownum as r FROM ( SELECT musicno, title, singer, album, genre, likesu, playsu, youtube, albumno, singerno, genreno FROM music
WHERE genreno= #{genreno}
WHERE genreno= #{genreno} AND (title LIKE '%' || #{word} || '%' OR singer LIKE '%' || #{word} || '%' OR album LIKE '%' || #{word} || '%' )
ORDER BY musicno DESC ) ) WHERE r >= #{startNum} AND r <= #{endNum}

SELECT COUNT(*) as cnt FROM music
WHERE genreno = #{genreno}
WHERE genreno = #{genreno} AND (title LIKE '%' || #{word} || '%' OR singer LIKE '%' || #{word} || '%' OR album LIKE '%' || #{word} || '%' )

mapper.update:
UPDATE music 
SET title=#{title}, singer=#{singer}, album=#{album}, genre=#{genre}
WHERE musicno=#{musicno}
